import React, { useState } from 'react';

const Product = ({ id: initialId, name: initialName, category: initialCategory, price: initialPrice, stockQuantity: initialStockQuantity, onDelete }) => {
  const [id, setId] = useState(initialId || null);
  const [name, setName] = useState(initialName || '');
  const [category, setCategory] = useState(initialCategory || '');
  const [price, setPrice] = useState(initialPrice || 0);
  const [stockQuantity, setStockQuantity] = useState(initialStockQuantity || 0);
  const [isEditing, setIsEditing] = useState(false);

  const handleEdit = () => setIsEditing(true);
  const handleSave = () => {
    console.log({ id, name, category, price, stockQuantity }); // For demonstration
    setIsEditing(false);
  };
  const handleDelete = () => onDelete(id);

  return (
    <div className="product">
      {isEditing ? (
        <>
          <input type="text" value={name} onChange={e => setName(e.target.value)} />
          <input type="text" value={category} onChange={e => setCategory(e.target.value)} />
          <input type="number" value={price} onChange={e => setPrice(e.target.value)} />
          <input type="number" value={stockQuantity} onChange={e => setStockQuantity(e.target.value)} />
          <button onClick={handleSave}>Save</button>
        </>
      ) : (
        <>
          <div>{name}</div>
          <div>{category}</div>
          <div>{price}</div>
          <div>{stockQuantity}</div>
          <button onClick={handleEdit}>Edit</button>
        </>
      )}
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
};

export default Product;
