import React, { useState } from 'react';

const Product = ({ product, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedProduct, setEditedProduct] = useState({ ...product });

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    // Perform validation if necessary

    // Update the product data
    // You can call an API here to update the product on the server
    // For simplicity, just log the edited data
    console.log("Updated Product:", editedProduct);

    setIsEditing(false);
  };

  const handleDelete = () => {
    // You can call an API here to delete the product on the server
    // For simplicity, just log the product ID
    console.log("Deleted Product ID:", product.id);
    onDelete(product.id);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  return (
    <div className="product">
      {isEditing ? (
        <>
          <input
            type="text"
            name="name"
            value={editedProduct.name}
            onChange={handleChange}
          />
          <input
            type="text"
            name="category"
            value={editedProduct.category}
            onChange={handleChange}
          />
          <input
            type="number"
            name="price"
            value={editedProduct.price}
            onChange={handleChange}
          />
          <input
            type="number"
            name="stockQuantity"
            value={editedProduct.stockQuantity}
            onChange={handleChange}
          />
          <button onClick={handleSave}>Save</button>
        </>
      ) : (
        <>
          <div>{product.name}</div>
          <div>{product.category}</div>
          <div>{product.price}</div>
          <div>{product.stockQuantity}</div>
          <button onClick={handleEdit}>Edit</button>
        </>
      )}
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
};

export default Product;
