// OrderCalendar.js
import React, { useState } from 'react';
import Calendar from 'react-calendar'; // Import the react-calendar library
import './Calendar.css'; // Import custom calendar styles

function OrderCalendar({ orders }) {
    const [selectedDate, setSelectedDate] = useState(new Date()); // State for selected date

    // Function to filter orders based on selected date
    const filteredOrders = orders.filter(order => {
        const orderDate = new Date(order.date);
        return orderDate.toDateString() === selectedDate.toDateString();
    });

    return (
        <div className="order-calendar">
            <h2>Orders Calendar View</h2>
            <div className="calendar-container">
                <Calendar
                    onChange={setSelectedDate}
                    value={selectedDate}
                />
            </div>
            <div className="orders-list">
                <h3>Orders for {selectedDate.toDateString()}</h3>
                {filteredOrders.length > 0 ? (
                    <ul>
                        {filteredOrders.map(order => (
                            <li key={order.id}>
                                <div>Customer: {order.customer}</div>
                                <div>Status: {order.status}</div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No orders for this date</p>
                )}
            </div>
        </div>
    );
}

export default OrderCalendar;
