// Product.js
import React from 'react';
import { Link } from 'react-router-dom';

const Product = ({ product, onClick }) => {
  // Add conditional check to ensure product is defined before accessing its properties
  if (!product || !product.name) {
    return <div>Error: Product data is missing or invalid</div>;
  }

  return (
    <div className="product-item" onClick={() => onClick(product)}>
      <h3 className="product-name">{product.name}</h3>
      <p className="product-details">Category: {product.category}</p>
      <p className="product-details">Price: ${product.price}</p>
      <p className="product-details">Stock Quantity: {product.stockQuantity}</p>
      <Link to={`/products/${product.id}`} className="product-link">View Details</Link>
    </div>
  );
};

export default Product;
