import React, { useState } from 'react';

const Product = ({ id, name, category, price, stockQuantity, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedName, setEditedName] = useState(name);
  const [editedCategory, setEditedCategory] = useState(category);
  const [editedPrice, setEditedPrice] = useState(price);
  const [editedStockQuantity, setEditedStockQuantity] = useState(stockQuantity);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    // Perform validation if necessary

    // Update the product data
    // You can call an API here to update the product on the server
    // For simplicity, just console.log the edited data
    console.log({
      id,
      name: editedName,
      category: editedCategory,
      price: editedPrice,
      stockQuantity: editedStockQuantity
    });

    setIsEditing(false);
  };

  const handleDelete = () => {
    // You can call an API here to delete the product on the server
    // For simplicity, just console.log the product ID
    console.log("Deleting product with ID:", id);
    onDelete(id);
  };

  return (
    <div className="product">
      {isEditing ? (
        <>
          <input
            type="text"
            value={editedName}
            onChange={(e) => setEditedName(e.target.value)}
          />
          <input
            type="text"
            value={editedCategory}
            onChange={(e) => setEditedCategory(e.target.value)}
          />
          <input
            type="number"
            value={editedPrice}
            onChange={(e) => setEditedPrice(e.target.value)}
          />
          <input
            type="number"
            value={editedStockQuantity}
            onChange={(e) => setEditedStockQuantity(e.target.value)}
          />
          <button onClick={handleSave}>Save</button>
        </>
      ) : (
        <>
          <div>{name}</div>
          <div>{category}</div>
          <div>{price}</div>
          <div>{stockQuantity}</div>
          <button onClick={handleEdit}>Edit</button>
        </>
      )}
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
};

export default Product;
