// ProductForm.js
import React, { useState } from 'react';
import './ProductForm.css'; // Import ProductForm component CSS

const ProductForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    category: '',
    price: '',
    stockQuantity: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData); // Invoke the onSubmit function with form data
    // Clear form fields after submission
    setFormData({
      name: '',
      category: '',
      price: '',
      stockQuantity: ''
    });
  };

  return (
    <div className="product-form-container">
      <h2 className="product-form-heading">Add New Product</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name" className="form-label">Product Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            className="form-input"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="category" className="form-label">Category:</label>
          <input
            type="text"
            id="category"
            name="category"
            className="form-input"
            value={formData.category}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="price" className="form-label">Price:</label>
          <input
            type="number"
            id="price"
            name="price"
            className="form-input"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="stockQuantity" className="form-label">Stock Quantity:</label>
          <input
            type="number"
            id="stockQuantity"
            name="stockQuantity"
            className="form-input"
            value={formData.stockQuantity}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="form-button">Add Product</button>
      </form>
    </div>
  );
};

export default ProductForm;
