// Product.js
import React from 'react';
import { Link } from 'react-router-dom';
import './Product.css';

const Product = ({ id, name, category, price, stockQuantity }) => {
  return (
    <div className="product-item">
      <h3 className="product-name">{name}</h3>
      <p className="product-details">Category: {category}</p>
      <p className="product-details">Price: ${price}</p>
      <p className="product-details">Stock Quantity: {stockQuantity}</p>
      <Link to={`/products/${id}`} className="product-link">View Details</Link>
    </div>
  );
};

export default Product;
