import React, { useState } from 'react';
import { Container, Table, Button, Modal, Form } from 'react-bootstrap';
import './OrderItem.css'; // Import OrderItem component CSS

const OrdersManagement = () => {
  // State for managing orders
  const [orders, setOrders] = useState([
      { id: 1, customer: 'Bala Krishna Reddy', date: '2024-03-15', status: 'Pending' },
      { id: 2, customer: 'Sudeep Reddy', date: '2024-03-16', status: 'Shipped' },
      { id: 3, customer: 'hello', date: '2024-03-17', status: 'Delivered' },
  ]);

  // State for managing modal
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({});

  // Function to handle form submission
  const handleSubmit = (e) => {
      e.preventDefault();
      const newOrder = { id: orders.length + 1, ...formData };
      setOrders([...orders, newOrder]);
      setShowModal(false);
  };

  const handleEdit = (id) => {
    // Implement edit functionality
    console.log('Edit order:', id);
  };

  const handleDelete = (id) => {
    // Implement delete functionality
    const updatedOrders = orders.filter(order => order.id !== id);
    setOrders(updatedOrders);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
      <Container fluid className="orders-container">
          <h1 className="orders-heading">Orders Management</h1>
          <Button onClick={() => setShowModal(true)} className="mb-3">Add New Order</Button>
          <Table striped bordered hover>
              <thead>
                  <tr>
                      <th>Order ID</th>
                      <th>Customer</th>
                      <th>Date</th>
                      <th>Status</th>
                      <th>Actions</th>
                  </tr>
              </thead>
              <tbody>
                  {orders.map(order => (
                      <tr key={order.id}>
                          <td>{order.id}</td>
                          <td>{order.customer}</td>
                          <td>{order.date}</td>
                          <td>{order.status}</td>
                          <td>
                            <button className="edit" onClick={() => handleEdit(order.id)}>Edit</button>
                            <button className="delete" onClick={() => handleDelete(order.id)}>Delete</button>
                          </td>
                      </tr>
                  ))}
              </tbody>
          </Table>

          {/* Modal for adding new order */}
          <Modal show={showModal} onHide={() => setShowModal(false)}>
              <Modal.Header closeButton>
                  <Modal.Title>Add New Order</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                  <Form onSubmit={handleSubmit}>
                      <Form.Group controlId="customer">
                          <Form.Label>Customer Name</Form.Label>
                          <Form.Control type="text" placeholder="Enter customer name" onChange={handleChange} />
                      </Form.Group>
                      <Form.Group controlId="date">
                          <Form.Label>Order Date</Form.Label>
                          <Form.Control type="date" onChange={handleChange} />
                      </Form.Group>
                      <Form.Group controlId="status">
                          <Form.Label>Status</Form.Label>
                          <Form.Control as="select" onChange={handleChange}>
                              <option>Pending</option>
                              <option>Shipped</option>
                              <option>Delivered</option>
                          </Form.Control>
                      </Form.Group>
                      <Button variant="primary" type="submit">
                          Submit
                      </Button>
                  </Form>
              </Modal.Body>
          </Modal>
      </Container>
  );
};

export default OrdersManagement;
