import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './Calendar.css';

const OrderCalendar = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [orders] = useState([
    { id: 1, date: '2024-01-09', status: 'Delivered' },
    { id: 2, date: '2024-01-09', status: 'Shipped' },
    { id: 3, date: '2024-01-10', status: 'Delivered' },
  ]);

  const handleDateChange = date => {
    setSelectedDate(date);
  };

  const filteredOrders = selectedDate
    ? orders.filter(order => order.date === selectedDate.toISOString().split('T')[0])
    : [];

  return (
    <div className="order-calendar">
      <h2 className="calendar-heading">Orders Calendar</h2>
      <div className="calendar-container">
        <Calendar onChange={handleDateChange} value={selectedDate} />
      </div>
      <div className="orders-list">
        {selectedDate ? (
          filteredOrders.length > 0 ? (
            <ul>
              {filteredOrders.map(order => (
                <li key={order.id}>{order.status} order</li>
              ))}
            </ul>
          ) : (
            <p>No orders for {selectedDate.toDateString()} are delivered.</p>
          )
        ) : (
          <p style={{ textAlign:"center", margin: '20px auto' }}>No date selected</p>
        )}
      </div>
    </div>
  );
};

export default OrderCalendar;
