import React, { useState } from 'react';
import Calendar from 'react-calendar'; // Assuming you're using the 'react-calendar' library

const OrderCalendar = () => {
    // Define state for the selected date
    const [selectedDate, setSelectedDate] = useState(new Date());

    // Define a function to handle date selection
    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    // Sample orders data - Replace this with your actual orders data
    const orders = [
        { id: 1, customer: 'Customer 1', date: new Date(2022, 10, 15), status: 'Pending' },
        { id: 2, customer: 'Customer 2', date: new Date(2022, 10, 16), status: 'Shipped' },
        { id: 3, customer: 'Customer 3', date: new Date(2022, 10, 17), status: 'Delivered' },
    ];

    // Filter orders based on selected date
    const filteredOrders = orders.filter(order => order.date.getDate() === selectedDate.getDate());

    // Determine if orders are delivered for the selected date
    const areOrdersDelivered = filteredOrders.some(order => order.status === 'Delivered');

    return (
        <div className="order-calendar">
            <h2 className="calendar-heading">Orders Calendar</h2>
            <div className="calendar-container">
                {/* Calendar component */}
                <Calendar
                    onChange={handleDateChange}
                    value={selectedDate}
                />
            </div>
            <div className="orders-list">
                {/* Display message indicating whether orders are delivered or not */}
                {areOrdersDelivered ? (
                    <p>Orders for {selectedDate.toDateString()} are delivered.</p>
                ) : (
                    <p>No orders for {selectedDate.toDateString()} are delivered.</p>
                )}
            </div>
        </div>
    );
};

export default OrderCalendar;
