import React, { useState } from 'react';
import './Productite.css'; // Import Product component CSS

const Product = ({ product, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedProduct, setEditedProduct] = useState({ ...product });

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    // Perform validation if necessary

    // Update the product data
    // You can call an API here to update the product on the server
    // For simplicity, just log the edited data
    console.log("Updated Product:", editedProduct);

    setIsEditing(false);
  };

  const handleDelete = () => {
    // You can call an API here to delete the product on the server
    // For simplicity, just log the product ID
    console.log("Deleted Product ID:", product.id);
    onDelete(product.id);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  return (
    <div className={`product ${isEditing ? 'editing' : ''}`}>
      {isEditing ? (
        <>
          <input
            type="text"
            name="name"
            value={editedProduct.name}
            onChange={handleChange}
            className="input-field"
          />
          <input
            type="text"
            name="category"
            value={editedProduct.category}
            onChange={handleChange}
            className="input-field"
          />
          <input
            type="number"
            name="price"
            value={editedProduct.price}
            onChange={handleChange}
            className="input-field"
          />
          <input
            type="number"
            name="stockQuantity"
            value={editedProduct.stockQuantity}
            onChange={handleChange}
            className="input-field"
          />
          <button onClick={handleSave} className="save-button">Save</button>
        </>
      ) : (
        <>
          <div className="product-info">{product.name}</div>
          <div className="product-info">{product.category}</div>
          <div className="product-info">{product.price}</div>
          <div className="product-info">{product.stockQuantity}</div>
          <button onClick={handleEdit} className="edit-button">Edit</button>
        </>
      )}
      <button onClick={handleDelete} className="delete-button">Delete</button>
    </div>
  );
};

export default Product;
