// product.js

// Import necessary dependencies
import React, { useState } from 'react';
import ProductItem from './ProductItem';
import ProductForm from './ProductForm';
import mockProducts from '../mockData/products';
import './Product.css'; // Import Product component CSS

// Define the Products component
function Products() {
    // State to manage product data
    const [products, setProducts] = useState(mockProducts);

    // Function to add a new product
    const addProduct = (newProduct) => {
        setProducts([...products, newProduct]);
    };

    return (
        <div className="products-container">
            <h1 className="products-heading">Products Management</h1>
            {/* Render the ProductForm component for adding new products */}
            <ProductForm onAddProduct={addProduct} />
            {/* Render a list of ProductItem components for each product */}
            <ul className="product-list">
                {products.map(product => (
                    <li key={product.id} className="product-item">
                        <ProductItem product={product} />
                    </li>
                ))}
            </ul>
        </div>
    );
}

// Export the Products component
export default Products;
