import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Dashboard from './Components/Dashboard';
const Product = ({ product }) => {
    return (
      <div className="product-item">
        {product.name && <h3 className="product-name">{product.name}</h3>}
        <p className="product-details">Category: {product.category}</p>
        <p className="product-details">Price: ${product.price}</p>
        <p className="product-details">Stock Quantity: {product.stockQuantity}</p>
        <Link to={`/products/${product.id}`} className="product-link">View Details</Link>
      </div>
    );
  };

import Orders from './Components/Order';
import './App.css'; // Import App component CSS

function App() {
    return (
        <Router>
            <div className="container">
                <header className="header">
                    <h1>ERP System With React</h1>
                </header>
                <nav className="navbar">
                    <a href="/">Dashboard</a>
                    <a href="/products">Products</a>
                    <a href="/orders">Orders</a>
                </nav>
                <Switch>
                    <Route path="/" exact component={Dashboard} />
                    <Route path="/products" component={Products} />
                    <Route path="/orders" component={Orders} />
                </Switch>
            </div>
        </Router>
    );
}

export default App;
